mapper.select:
SELECT r.roomno, r.roomtype, r.title, r.floor, r.area, r.fee, DATE_FORMAT(confirmdate, '%Y.%m.%d') AS confirmdate, p.dealingtype, p.deposit, p.price, r.region_2depth_name, r.latitude, r.longitude, i.filename FROM room AS r INNER JOIN (SELECT * FROM price GROUP BY roomno) AS p ON r.roomno=p.roomno INNER JOIN (SELECT * FROM image GROUP BY roomno) AS i ON r.roomno=i.roomno
p.price between #{monthFrom} and #{monthTo} AND p.dealingtype="월세" AND ((p.price BETWEEN #{depositFrom} and #{depositTo} AND p.dealingtype="전세") OR (p.deposit BETWEEN #{depositFrom} and #{depositTo}))
OR (p.price between #{buyingFrom} and #{buyingTo} AND p.dealingtype="매매")
AND r.fee between #{feeFrom} and #{feeTo}
AND r.area between #{sizeFrom} and #{sizeTo}
AND r.roomtype LIKE concat('%', #{roomtype}, '%')
AND p.dealingtype LIKE concat('%', #{dealingtype}, '%')
AND region_2depth_name LIKE concat('%', #{region_2depth_name}, '%')
AND r.latitude between #{west} and #{east}
AND r.longitude between #{south} and #{north}
ORDER BY r.roomno ASC
LIMIT #{offset}, #{listCount}

SELECT COUNT(r.roomno) FROM room AS r INNER JOIN (SELECT * FROM price GROUP BY roomno) AS p ON r.roomno=p.roomno
p.price between #{monthFrom} and #{monthTo} AND p.dealingtype="월세" AND ((p.price BETWEEN #{depositFrom} and #{depositTo} AND p.dealingtype="전세") OR (p.deposit BETWEEN #{depositFrom} and #{depositTo}))
OR (p.price between #{buyingFrom} and #{buyingTo} AND p.dealingtype="매매")
AND r.fee between #{feeFrom} and #{feeTo}
AND r.area between #{sizeFrom} and #{sizeTo}
AND r.roomtype LIKE concat('%', #{roomtype}, '%')
AND p.dealingtype LIKE concat('%', #{dealingtype}, '%')
AND region_2depth_name LIKE concat('%', #{region_2depth_name}, '%')
AND r.latitude between #{west} and #{east}
AND r.longitude between #{south} and #{north}
ORDER BY r.roomno ASC

SELECT
roomno, roomtype, latitude, longitude, region_2depth_name
FROM room;
